    private ArrayList<ElectrodinamicFragment.WeatherItem> generateData() {
        ArrayList<ElectrodinamicFragment.WeatherItem> list = new ArrayList<>();
        for(int i = 0; i< text.length; i++) {
            list.add(new WeatherItem(text[i],image_urls[i]));
        }
        return list;
    }

